/**
     * @return the list of all dataset boxes present on the graphic view
     */
    public List<DataSetBaseBox> getDataSetBoxes()
    {
        List<DataSetBaseBox> dataSetBaseBoxes = new List<DataSetBaseBox>();  //Fixed
        for (Component c : getComponents())
        {
            if (c instanceof DataSetBaseBox)
            {
                dataSetBaseBoxes.add((DataSetBaseBox)c);
            }
        }
        return dataSetBaseBoxes;
    }

