remediationSample:
@Remote
public interface MyRemoteBean {
    public int aMethod();
    public void removeMethod();
}

@Stateful
public class MyBean implements MyRemoteBean {
    ...
    public int aMethod() {
        ...
    }

    @Remove // defining the remove method
    public void removeMethod() {
        ...
    }
}

public class Client {

    private static @EJB MyRemoteBean myClientBean;

    public static void main(String[] args) {
        Client client = new Client(args);
        client.callServer();
    }

    public Client(String[] args) {}

    public void callServer() {

        try {
            int res=myClientBean.aMethod();
            myClientBean.removeMethod(); // FIXED : Calling the remove method
        }catch(Exception e){
            e.printStackTrace();
        }
    }
}

